def calculate_sentence_prob(sentence: list[str], model: defaultdict[Counter]) -> float:
    prob = 0
    for w1, w2, w3 in ngrams(sentence, n=3):
        try:
            prob += np.log(model[w1,w2][w3])
        except KeyError:
            # OOV
            p += 0.0
    return prob

